UIPluginV2.UIComponentJS:
		function connectCallback() {
			if(!!PluginManager.userImage.path) {
				document.getElementById('welcomeUser_userImage').src = PluginManager.userImage.path;
			}

			document.getElementById('welcomeUser_userName').innerHTML = PluginManager.userName;
			document.getElementById('welcomeUser_userName').style = `
				font: ${PluginManager.pluginConfiguration.FontMedium18};
				color: ${PluginManager.pluginConfiguration.Color4};
			`;

			document.getElementById('welcomeUser_userMessage').innerHTML = PluginManager.userMessage;
			document.getElementById('welcomeUser_userMessage').style = `
				font: ${PluginManager.pluginConfiguration.FontRegular12};
				color: ${PluginManager.pluginConfiguration.Color22};
			`;

			const prevDayButton = document.getElementById('welcomeUser_prevDayButton')
			prevDayButton.style = `background: ${PluginManager.pluginConfiguration.Color1};`;

			const prevDayButtonImg = document.getElementById('welcomeUser_prevDayButtonImg');
			prevDayButtonImg.src = PluginManager.previousDayIcon;
			prevDayButtonImg.style = `
				background: ${PluginManager.pluginConfiguration.Color6};
				width: 36px;
				height: 36px;
				border-radius: 18px;
				margin-left: -9px;
			`

			const cardDate = document.getElementById('welcomeUser_cardDate');
			cardDate.innerHTML = PluginManager.selectedDate;
			cardDate.style = `
				font: ${PluginManager.pluginConfiguration.FontMedium14};
				color: ${PluginManager.pluginConfiguration.Color8};
			`;

			const todayContainer = document.getElementById('welcomeUser_todayContainer');
			todayContainer.style = `
				background-color: ${PluginManager.pluginConfiguration.Color6};
				height: 34px;
				width: 100%;
				align-content: center;
				align-self: center;
				border-radius: 17px;
				margin-top: -5px;
			`;


			const todayButton = document.getElementById('welcomeUser_todayButton');
			todayButton.style = `
				background-color: ${PluginManager.pluginConfiguration.Color6};
				height: 34px;
				width: 100%;
				text-align: center;
				align-self: center;
				vertical-align: middle;
				border-radius: 17px;
			`;
			todayButton.addEventListener('click', function(e) {
				PluginManager.CardDate.previousDay();
				PluginManager.throwEvent('nextDay');
				PluginManager.userName = "CHANGE";
			})


			const todayButtonImage = document.getElementById('welcomeUser_todayButtonImage');
			todayButtonImage.src = PluginManager.todayIcon;
			todayButtonImage.style = `
				width: 16px;
				display: inline-block;
				margin-top: -4.5px;
			`;

			const todayLabel = document.getElementById('welcomeUser_todayLabel');
			todayLabel.innerHTML = PluginManager.todayLabel;
			todayLabel.style = `
				font: ${PluginManager.pluginConfiguration.FontRegular12};
				color: ${PluginManager.pluginConfiguration.Color22};
				display: inline-block;
				align-self: center;
				text-align: center;
				vertical-align: middle;
				padding-left: 6px;
				margin-top: -6px;
			`;

			const nextDayButton = document.getElementById('welcomeUser_nextDayButton');
			nextDayButton.style = `background: ${PluginManager.pluginConfiguration.Color1}`;

			const nextDayButtonImg = document.getElementById('welcomeUser_nextDayButtonImg');
			nextDayButtonImg.src = PluginManager.nextDayIcon;
			nextDayButtonImg.style = `
				font: ${PluginManager.pluginConfiguration.FontRegular14};
				background: ${PluginManager.pluginConfiguration.Color6};
				width: 36px;
				height:36px;
				border-radius: 18px;
				margin-right: -8.9px;
			`;

			const countersContainer = document.getElementById('welcomeUser_countersContainer');
			countersContainer.style = `
				background-color: ${PluginManager.pluginConfiguration.Color6};
				height: 36px;
				width: 90%;
				align-content: center;
				align-self: center;
				border-radius: 18px;
			`;

			const visitCounterContainer = document.getElementById('welcomerUser_visitCounterContainer');
			visitCounterContainer.style = `
				display: flex;
				justify-content: center;
				align-self: center;
			`;

			const visitCounterInnerContainer = document.getElementById('welcomerUser_visitCounterInnerContainer');
			visitCounterInnerContainer.style = `
				text-align: center;
				align-self: center;
				vertical-align: middle;
				padding-left: 5px;
			`;

			const visitCounterValue = document.getElementById('welcomerUser_visitCounterValue');
			visitCounterValue.innerHTML = PluginManager.visitCount;
			visitCounterValue.style = `
				font: 24px Regular;
				color: ${PluginManager.pluginConfiguration.Color4};
				display: table-cell;
				align-self: center;
			`;

			const visitCounterLabel = document.getElementById('welcomerUser_visitCounterLabel');
			visitCounterLabel.innerHTML = PluginManager.visitsLabel;
			visitCounterLabel.style = `
				font: ${PluginManager.pluginConfiguration.FontRegular12};
				color: ${PluginManager.pluginConfiguration.Color22};
				display: table-cell;
				align-self: center;
				text-align: center;
				vertical-align: middle;
				padding-left: 5px;
			`;

			const counterBar01 = document.getElementById('welcomeUser_counterBar01');
			counterBar01.style = `
				font: ${PluginManager.pluginConfiguration.FontRegular14};
				color: ${PluginManager.pluginConfiguration.Color1};
				display: table-cell;
				text-align: center;
				vertical-align: middle;
				align-self: center;
				padding-left: 2px;
			`;

			const issueCounterInnerContainer = document.getElementById('welcomerUser_issueCounterInnerContainer');
			issueCounterInnerContainer.style = `
				text-align: center;
				overflow: auto;
				width: 38%;
				align-self: center;
				vertical-align: middle;
				padding-left: 5px;
			`;

			const issueCounterValue = document.getElementById('welcomerUser_issueCounterValue');
			issueCounterValue.innerHTML = PluginManager.issuesCount;
			issueCounterValue.style = `
				font: 24px Regular;
				color: ${PluginManager.pluginConfiguration.Color4};
				display: table-cell;
				align-self: center;
			`;

			const issueCounterLabel = document.getElementById('welcomerUser_issueCounterLabel');
			issueCounterLabel.innerHTML = PluginManager.customerTasksLabel
			issueCounterLabel.style = `
				font: ${PluginManager.pluginConfiguration.FontRegular12};
				color: ${PluginManager.pluginConfiguration.Color22};
				display: table-cell;
				align-self: center;
				text-align: center;
				vertical-align: middle;
				padding-left: 5px;
			`;

			const counterBar02 = document.getElementById('welcomeUser_counterBar02');
			counterBar02.style = `
				font: ${PluginManager.pluginConfiguration.FontRegular14};
				color: ${PluginManager.pluginConfiguration.Color1};
				display: table-cell;
				text-align: center;
				vertical-align: middle;
				align-self: center;
				padding-left: 2px;
			`;

			const taskCounterInnerContainer = document.getElementById('welcomerUser_taskCounterInnerContainer');
			taskCounterInnerContainer.style = `
