		function renderCallback() {
			document.getElementById('welcomeUser_userName').innerHTML = PluginManager.userName;
		}

		function previousDayButtonImageChange(e) {
			PluginManager.CardDate.previousDay();
			//this.setState({previousDay: this.props.previousDayOnTouchIcon});
			// this.props.CardDate.previousDay(); 
		}
